document.addEventListener("DOMContentLoaded", function () {
    const audio = document.getElementById("bgmMusic");

    // Ajusta el volumen al 50%
    if (audio) {
        audio.volume = 0.5;

        // Espera la interacción del usuario para reproducir el audio
        const playAudio = () => {
            audio.play().catch((error) => {
                console.log("Error al intentar reproducir el audio:", error);
            });
            // Elimina el evento después de la interacción
            document.removeEventListener("click", playAudio);
        };

        // Agrega un evento de clic para iniciar la reproducción
        document.addEventListener("click", playAudio);
    }
});